//円と直線の交点を求める関数
vector<Point> getCrossPoints(Circle c,Line l){

	vector<Point> ret;

	Vector pr = project(l,c.center);
	Vector e = (l.p[1]-l.p[0])/abs(l.p[1]-l.p[0]);

	double base;

	if(fabs(c.r*c.r-norm(pr-c.center)) < EPS){

		base = 0;
	}else{
		base = sqrt(c.r*c.r-norm(pr-c.center));
	}

	ret.push_back(Point(pr+e*base));
	ret.push_back(Point(pr-e*base));

	return ret;
}

vector<Point> getContact(Circle circle,Point point){

	double k = circle.center.x - point.x;
	double X = point.y - circle.center.y;

	double A = k*k - circle.r*circle.r;

	if(fabs(A) < EPS){

		double slope = (circle.r*circle.r-X*X)/(2*k*X);

		if(fabs(slope) < EPS){

			vector<Point> ret;

			ret.push_back(Point(point.x,sqrt(circle.r*circle.r-(point.x-circle.center.x)*(point.x-circle.center.x))+circle.center.y));
			ret.push_back(Point(sqrt(circle.r*circle.r-(point.y-circle.center.y)*(point.y-circle.center.y))+circle.center.x,point.y));

			return ret;
		}

		Line line;

		line.p[0] = point;
		line.p[1] = Point(point.x+NUM,point.y+NUM*slope);

		return getCrossPoints(circle,line);
	}

	double B = 2*k*X;
	double C = X*X - circle.r*circle.r;

	double slope_1 = (-B+sqrt(B*B-4*A*C))/(2*A);
	double slope_2 = (-B-sqrt(B*B-4*A*C))/(2*A);

	Line line_1,line_2;

	line_1.p[0] = point;
	line_1.p[1] = Point(point.x+NUM,point.y+NUM*slope_1);
	vector<Point> ret_1 = getCrossPoints(circle,line_1);

	line_2.p[0] = point;
	line_2.p[1] = Point(point.x+NUM,point.y+NUM*slope_2);

	vector<Point> ret_2 = getCrossPoints(circle,line_2);

	vector<Point> ret;

	ret.push_back(ret_1[0]);
	ret.push_back(ret_2[0]);

	return ret;
}

