	scanf("%lf %lf",&point.x,&point.y);
	scanf("%lf %lf %lf",&circle.center.x,&circle.center.y,&circle.r);

	vector<Point> ans = getContact(circle,point);

	if(fabs(ans[0].x-ans[1].x) > EPS){

		if(ans[0].x > ans[1].x+EPS){

			swap(ans[0],ans[1]);
		}

	}else{

		if(ans[0].y > ans[1].y+EPS){

			swap(ans[0],ans[1]);
		}
	}

	printf("%.10lf %.10lf\n",ans[0].x,ans[0].y);
	printf("%.10lf %.10lf\n",ans[1].x,ans[1].y);

	return 0;
}

