typedef double db;
struct P{
    db x, y;
    P(){}
    P(db x, db y) : x(x) , y(y) {}
    P operator - (P a){ return P(x-a.x, y-a.y); }
    db dot(P a){ return a.x*x + a.y*y; }
    P operator * (db a) { return P(a*x, a*y); }
    P operator + (P a){ return P(x+a.x, y+a.y); }
    db times(P a){ return x*a.y-y*a.x; }
    db clen() { return sqrt( x*x+y*y ); }
    db l2() { return x*x+y*y; }
    P rot90(){
        db tx = x, ty = y;
        x = -ty;
        y = tx;
        return P(x,y);   
    }
    void swap(P& a){
        db tx = x, ty = y;
        x = a.x;  y = a.y;
        a.x = tx; a.y = ty;
    }
    bool operator < (const P a) const { return (x==a.x)?(y<a.y):(x<a.x); }
