    P p = P(x, y);
    cin >> x >> y >> r;
    C c = C(P(x, y), r);
    P z1, z2;
    tangent(c, p, z1, z2);
    tie(z1, z2) = minmax(z1, z2, rless);
    cout << z1.x << " " << z1.y << endl;
    cout << z2.x << " " << z2.y << endl;
    return 0;
}

