using namespace std;

struct Point {
    double x, y;

    Point() = default;
    Point(double x, double y) : x(x), y(y) {}

    Point operator+(const Point &rhs) const {
        return Point(x + rhs.x, y + rhs.y);
    }

    Point operator-(const Point &rhs) const {
        return Point(x - rhs.x, y - rhs.y);
    }

    double operator*(const Point &rhs) const {
        return x * rhs.x + y * rhs.y;
    }

    double operator%(const Point &rhs) const {
        return x * rhs.y - y * rhs.x;
    }

    bool operator<(const Point &rhs) const {
        return fabs(x - rhs.x) < 1e-9 ? y < rhs.y : x < rhs.x;
    }

    Point operator/(double d) const {
        return Point(x / d, y / d);
    }

    Point operator*(double d) const {
        return Point(x * d, y * d);
    }
};

array<Point, 2> tangent(Point o, double r, Point p) {
    double dist = sqrt((p - o) * (p - o));
    double len = sqrt(dist * dist - r * r);
    double ang = acos(len / dist);
    Point vec = (o - p) / dist * len;
    array<Point, 2> res;
    for (int i = 0; i < 2; ++i) {
        int z = i == 0 ? 1 : -1;
        Point v(vec.x * cos(z * ang) - vec.y * sin(z * ang), vec.x * sin(z * ang) + vec.y * cos(z * ang));
        res[i] = p + v; 
    }
    return res;
}

int main() {
    Point p, o;
    cin >> p.x >> p.y;
    cin >> o.x >> o.y;
    double r; cin >> r;
    array<Point, 2> ans = tangent(o, r, p);
    if (ans[1] < ans[0]) swap(ans[0], ans[1]);
    cout << fixed << setprecision(20) << ans[0].x << ' ' << ans[0].y << endl;
    cout << fixed << setprecision(20) << ans[1].x << ' ' << ans[1].y << endl;
    return 0;
}

