pair<P,P> tangent(const C &c,const P &p){
  double d = distancePP(c.p,p);
  double k = sqrt( - c.r * c.r + d * d);
  double C = asin(c.r/d);
  double t = arg(c.p - p);
  P A = p + polar(k,t+C),B = p + polar(k,t-C);
  if(A < B)
    return mp(A,B);
  return mp(B,A);
}

