    int m = n / 2;
    double x = a[m].first;
    double d = min(closest_pair(a, m), closest_pair(a + m, n - m)); // x軸で区切られた範囲内での頂点対のみ考慮
    inplace_merge(a, a + m, a + n, comp_y); // ソート済みの[a, a+m)と[a+m, a+n)をy座標基準でマージ

    vector<p> b; // 直線 x = a[m].firstから距離d未満の点を入れる
    for (int i = 0; i < n; i++) {
        if (fabs(a[i].first - x) >= d) continue; // そもそもx座標の差がd以上の点は論外

        // bに入っている頂点を末尾から、y座標の差がd以上になるまで見ていく
        // y座標降順で見ていき、d以上離れた時点で打ち切り
        for (int j = 0; j < b.size(); j++) {
            double dx = a[i].first - b[b.size() - 1 - j].first;
            double dy = a[i].second - b[b.size() - 1 - j].second;
            if (dy >= d) break;
            d = min(d, sqrt(dx * dx + dy * dy));
        }

        b.push_back(a[i]);
    }
    return d;
}


struct STsum {
private:
    int n;
    vector<int> dat;
public:
    STsum(int siz) {
        n = 1;
        while (n < siz) n *= 2;
        dat.resize(2 * n - 1, 0);
    }

    void update(int x, int val) {
        x += (n - 1);
        dat[x] += val;
        while (x > 0) {
            x = (x - 1) / 2;
            dat[x] = dat[2 * x + 1] + dat[2 * x + 2];
        }
    }

    // focus on k-th node, who controls [l, r)
    int query(int a, int b, int k = 0, int l = 0, int r = -1) {
        if (r < 0) r = n;
        if (r <= a || b <= l) return 0;
        if (a <= l && r <= b) return dat[k];

        int lx = query(a, b, 2 * k + 1, l, (l + r) / 2);
        int rx = query(a, b, 2 * k + 2, (l + r) / 2, r);
        return lx + rx;
    }
};

struct Circle {
    Point c;
    double r;

    Circle(Point c = Point(), double r = 0.0) : c(c), r(r) {}
};


Point project(Segment s, Point p) {
    Vector base = s.p2 - s.p1;
    double r = dot(p - s.p1, base) / norm(base);
    return s.p1 + base * r;
}

Point reflect(Segment s, Point p) {
    return p + (project(s, p) - p) * 2.0;
}

double arg(Vector p) {
    return atan2(p.y, p.x);
}

Point polar2(double a, double r){
    return Point(a * cos(r), a * sin(r));
}

pair<Point, Point> getCrossPoints(Circle c1, Circle c2) {
    double d = abs(c1.c - c2.c);
    double a = acos((c1.r * c1.r + d * d - c2.r * c2.r) / (2 * c1.r * d));
    double t = arg(c2.c - c1.c);
    return {c1.c + polar2(c1.r, t + a), c1.c + polar2(c1.r, t - a)};
}

pair<Point,Point> getContactPoints(Circle c, Point p){
    assert(c.r < getDistance(c.c, p));
    double d = getDistance(c. c, p);
    return getCrossPoints(c, Circle(p, sqrt(d*d-c.r*c.r)));
}

int main(){
    double x, y, cx, cy, r;
    cin >> x >> y >> cx >> cy >> r;
    pair<Point,Point> p = getContactPoints(Circle(Point(cx,cy),r), Point(x,y));
    if(p.second < p.first)  swap(p.first, p.second);
    cout << fixed << setprecision(12);
    cout << p.first.x << " " << p.first.y << endl;
    cout << p.second.x << " " << p.second.y << endl;
    return 0;
}

