        len = sqrt(pow(px - cx, 2) + pow(py - cy, 2));
        s = atan2(py - cy, px- cx);
        f = acos(r / len);
        ax[0] = r * cos(s + f);
        ay[0] = r * sin(s + f);
        ax[1] = r * cos(s - f);
        ay[1] = r * sin(s - f);
        if(ax[0] < ax[1] || (ax[0] == ax[1] && ay[0] < ay[1])){
                printf("%.6f %.6f\n%.6f %.6f\n",
                           ax[0] + cx, ay[0] + cy, ax[1] + cx, ay[1] + cy);
        }else{
                printf("%.6f %.6f\n%.6f %.6f\n",
               ax[1] + cx, ay[1] + cy, ax[0] + cx, ay[0] + cy);
        }
}

