                a = pow((ty / tx), 2) + 1;
                b = pow(r, 2) * ty / pow(tx, 2);
                c = pow(pow(r, 2) / tx, 2) - pow(r, 2);
                ay[0] = (b + sqrt(pow(b, 2) - a * c)) / a;
                ay[1] = (b - sqrt(pow(b, 2) - a * c)) / a;
