                ax[1] = -ty / tx * ay[1] + pow(r, 2) / tx;
        }else{
                ay[0] = ay[1] = pow(r, 2) / ty;
                ax[0] = sqrt(pow(r, 2) - pow(ay[0], 2));
                ax[1] = -sqrt(pow(r, 2) - pow(ay[1], 2));
