    enum ST { Bottom, Left, Right, Top }

    class Program
    {
        static void Main(string[] args)
        {
            double[] input = Array.ConvertAll(Console.ReadLine().Split(' '), double.Parse);
            Point p = new Point(input[0], input[1]);

            input = Array.ConvertAll(Console.ReadLine().Split(' '), double.Parse);
            Circle c = new Circle(new Point(input[0], input[1]), input[2]);

            var points = Contact(c, p);

            StringBuilder sb = new StringBuilder();
            foreach (var point in points) sb.AppendLine(point.ToString());
            Console.Write(sb);
        }

        static Point[] Contact(Circle c, Point p)
        {
            Point cp = p - c.p;
            double d = Math.Sqrt(cp.Norm - c.r * c.r);
            double k = c.r / cp.Norm;
            double r = c.r * k;
            double ds = d * k;

            double ax = c.p.x + cp.x * r - cp.y * ds;
            double ay = c.p.y + cp.y * r + cp.x * ds;
            double bx = c.p.x + cp.x * r + cp.y * ds;
            double by = c.p.y + cp.y * r - cp.x * ds;

            Point[] ret = new Point[] { new Point(ax, ay), new Point(bx, by) };
            Array.Sort(ret);
            return ret;
        }
    }

    class EndPoint : IComparable
    {
        internal Point p;
        internal int seg;
        internal ST st;

        public EndPoint(Point p, int seg, ST st)
        {
            this.p = p;
            this.seg = seg;
            this.st = st;
        }

        public int CompareTo(object obj)
        {
            EndPoint other = obj as EndPoint;

            if (p.y < other.p.y) return -1;
            if (p.y > other.p.y) return 1;
            if (st < other.st) return -1;
            if (st > other.st) return 1;
            return 0;
        }
    }

    class Point : IComparable
    {
        static readonly double EPS = 1E-10;

        internal double x, y;

        public Point(double x, double y)
        {
            this.x = x;
            this.y = y;
        }

        public double Abs { get { return Math.Sqrt(Norm); } }
        public double Norm { get { return x * x + y * y; } }

        public double Dot(Point p) { return x * p.x + y * p.y; }
        public double Cross(Point p) { return x * p.y - y * p.x; }
        public double Distance(Point p) { return (this - p).Abs; }

        public static Point operator +(Point a, Point b) { return new Point(a.x + b.x, a.y + b.y); }
        public static Point operator -(Point a, Point b) { return new Point(a.x - b.x, a.y - b.y); }
        public static Point operator *(Point p, double d) { return new Point(p.x * d, p.y * d); }
        public static Point operator /(Point p, double d) { return new Point(p.x / d, p.y / d); }
        public static bool operator <(Point a, Point b) { return Math.Abs(a.x - b.x) > EPS ? a.x < b.x : a.y < b.y; }
        public static bool operator >(Point a, Point b) { return Math.Abs(a.x - b.x) > EPS ? a.x > b.x : a.y > b.y; }
        public static bool operator ==(Point a, Point b) { return Math.Abs(a.x - b.x) < EPS && Math.Abs(a.y - b.y) < EPS; }
        public static bool operator !=(Point a, Point b) { return Math.Abs(a.x - b.x) >= EPS || Math.Abs(a.y - b.y) >= EPS; }

        public int CompareTo(object obj)
        {
            var other = obj as Point;
            if (obj == null) return -1;

            ComparerX compX = new ComparerX();
            return compX.Compare(this, other);
        }

        public override bool Equals(object obj)
        {
            var other = obj as Point;
            return Math.Abs(x - other.x) < EPS && Math.Abs(y - other.y) < EPS;
        }

        public override int GetHashCode()
        {
            return x.GetHashCode() ^ y.GetHashCode();
        }

        public override string ToString()
        {
            return string.Format("{0:f10} {1:f10}", x, y);
        }
    }

    class ComparerX : IComparer<Point>
    {
        static readonly double EPS = 1E-10;

        public int Compare(Point a, Point b)
        {
            if (a.x - b.x < -EPS) return -1;
            if (a.x - b.x > EPS) return 1;
            if (a.y - b.y < -EPS) return -1;
            if (a.y - b.y > EPS) return 1;
            return 0;
        }
    }

    class ComparerY : IComparer<Point>
    {
        static readonly double EPS = 1E-10;

        public int Compare(Point a, Point b)
        {
            if (a.y - b.y < -EPS) return -1;
            if (a.y - b.y > EPS) return 1;
            if (a.x - b.x < -EPS) return -1;
            if (a.x - b.x > EPS) return 1;
            return 0;
        }
    }

    class Segment
    {
        internal Point p1, p2;

        public Segment(Point p1, Point p2)
        {
            this.p1 = p1;
            this.p2 = p2;
        }
    }

    class Circle
    {
        internal Point p;
        internal double r;

        public Circle(Point p, double r)
        {
            this.p = p;
            this.r = r;
        }
    }
}

