static class Exg{
	static public Pair[] Gp13(Pair p1,double r,Pair a){
		a-=p1;
		double g=a.x*a.x+a.y*a.y,k=Sqrt(g-r*r);
		return new Pair[]{new Pair(r*(a.x*r+a.y*k)/g,r*(a.y*r-a.x*k)/g)+p1,new Pair(r*(a.x*r-a.y*k)/g,r*(a.y*r+a.x*k)/g)+p1};
	}
}
