  vec1 = multiplyVector(vec1,circle.r);
  var points = [];
  points[0] = addPoint(circle.center,getRotateVectorFromRad(vec1,radian));
  points[1] = addPoint(circle.center,getRotateVectorFromRad(vec1,-radian));
  return points;
}

