mod complex {
    use std::fmt::Debug;
    use std::ops::{Add, Div, Mul, Sub};

    #[derive(PartialEq, Eq, Copy, Clone, Hash, Debug, Default)]
    #[repr(C)]
    pub struct Complex<T> {
        /// Real portion of the complex number
        pub re: T,
        /// Imaginary portion of the complex number
        pub im: T,
    }

    impl<T> Complex<T> {
        #[inline]
        pub fn new(re: T, im: T) -> Self {
            Complex { re, im }
        }
    }

    impl Complex<f64> {
        #[inline]
        pub fn conj(&self) -> Self {
            Self::new(self.re.clone(), -self.im.clone())
        }

        #[inline]
        pub fn norm(&self) -> f64 {
            self.re.hypot(self.im)
        }

        #[inline]
        pub fn norm_sqr(&self) -> f64 {
            self.re.clone() * self.re.clone() + self.im.clone() * self.im.clone()
        }

        #[inline]
        pub fn arg(&self) -> f64 {
            self.im.atan2(self.re)
        }

        #[inline]
        pub fn from_polar(r: &f64, theta: &f64) -> Complex<f64> {
            Complex::new(*r * theta.cos(), *r * theta.sin())
        }
    }

    impl<T: Add<Output = T>> Add for Complex<T> {
        type Output = Self;
        fn add(self, rhs: Self) -> Self::Output {
            Self::new(self.re + rhs.re, self.im + rhs.im)
        }
    }

    impl<T: Sub<Output = T>> Sub for Complex<T> {
        type Output = Self;
        fn sub(self, rhs: Self) -> Self::Output {
            Self::new(self.re - rhs.re, self.im - rhs.im)
        }
    }

    impl<T: Copy + Add<Output = T> + Sub<Output = T> + Mul<Output = T>> Mul for Complex<T> {
        type Output = Self;
        fn mul(self, rhs: Self) -> Self::Output {
            Self::new(
                self.re * rhs.re - self.im * rhs.im,
                self.re * rhs.im + self.im * rhs.re,
            )
        }
    }

    impl<T: Copy + Mul<Output = T>> Mul<T> for Complex<T> {
        type Output = Self;
        fn mul(self, rhs: T) -> Self::Output {
            Self::new(self.re * rhs, self.im * rhs)
        }
    }

    // impl<T: Copy + Mul<Output = T>> Mul<Complex<T>> for T {
    //     type Output = Complex<T>;
    //     fn mul(self, rhs: Complex<T>) -> Self::Output {
    //         Self::new(self * rhs.re, self * rhs.im)
    //     }
    // }

    impl Mul<Complex<f64>> for f64 {
        type Output = Complex<f64>;
        fn mul(self, rhs: Complex<f64>) -> Self::Output {
            Complex::new(self * rhs.re, self * rhs.im)
        }
    }

    impl<T: Copy + Add<Output = T> + Sub<Output = T> + Mul<Output = T> + Div<Output = T>> Div
        for Complex<T>
    {
        type Output = Self;
        fn div(self, rhs: Self) -> Self::Output {
            let d = rhs.re * rhs.re + rhs.im * rhs.im;
            Self::new(
                (self.re * rhs.re + self.im * rhs.im) / d,
                (self.im * rhs.re - self.re * rhs.im) / d,
            )
        }
    }

    impl<T: Copy + Div<Output = T>> Div<T> for Complex<T> {
        type Output = Self;
        fn div(self, rhs: T) -> Self::Output {
            Self::new(self.re / rhs, self.im / rhs)
        }
    }
}

