#define pb push_back
#define mp make_pair

map<ll, int> make_map(vector<ll>& As) {
    map<ll, int> ret;
    for(auto a: As) ++ret[a];
    return ret;
}

map<ll, int> make_combination(map<ll, int>& As, map<ll, int>& Bs) {
    map<ll, int> ret;
    for(auto a: As) {
        for(auto b: Bs) {
            auto val = a.first + b.first;
            auto cnt = a.second * b.second;
            ret[val] += cnt;
        }
    }
    return ret;
}

ll coin_combination(ll N, ll V,
                    vector<ll>& As, vector<ll>& Bs,
                    vector<ll>& Cs, vector<ll>& Ds) {
    auto Amap = make_map(As);
    auto Bmap = make_map(Bs);
    auto Cmap = make_map(Cs);
    auto Dmap = make_map(Ds);
    auto ABmap = make_combination(Amap, Bmap);
    auto CDmap = make_combination(Cmap, Dmap);

    ll ret = 0;
    for (auto ab: ABmap) {
        auto val = ab.first;
        auto cnt = ab.second;
        // cout << "val, cnt = " << val << "," << cnt << endl;
        if (CDmap.count(V - val)) {
            // cout << "val, cnt1, cnt2 = " << val << "," << cnt << "," << CDmap[V - val] << endl;
            ret += cnt * CDmap[V - val];
        }
    }

    return ret;
}

ll largest_square(const ll N, const vector<ll>& in_histo) {
    // 番兵として、右端に0を入れる
    auto histo = in_histo;
    histo.pb(0);

    ll ans = 0;
    // <ヒストグラムの高さ, index>
    stack<pair<ll, ll>> s;
    REP(n, N+1) {
        auto height = histo[n];
        if (s.empty()) s.push(mp(height, n));
        else if (s.top().first < height) s.push(mp(height, n));
        else if (s.top().first > height) {
            ll last_idx = n;
            while(!s.empty() && s.top().first > height) {
                auto area = (n - s.top().second) * s.top().first;
                ans = max(area, ans);
                last_idx = s.top().second;
                s.pop();
            }
            s.push(mp(height, last_idx));
        }
    }
    return ans;
}

int main() {
    ll N, V;
    cin >> N >> V;
    vector<ll> As(N);
    vector<ll> Bs(N);
    vector<ll> Cs(N);
    vector<ll> Ds(N);
    REP(n, N) cin >> As[n];
    REP(n, N) cin >> Bs[n];
    REP(n, N) cin >> Cs[n];
    REP(n, N) cin >> Ds[n];
    cout << coin_combination(N, V, As, Bs, Cs, Ds) << endl;

    return 0;
}

