void calc(int x,int y) {

	for (size_t i = 1; i <= n; i++)
	{
		for (size_t j= 1; j <= n; j++)
		{
			d[i][j] = INF;
		}
	}

	priority_queue<vector<int>, vector<vector<int>>, greater<vector<int>>> q;
	q.push({0,x,y});
	while (!q.empty()) {
		auto p = q.top();
		q.pop();
		int c = p[0];
		int fx = p[1];
		int fy = p[2];
		if (d[fx][fy] <= c) continue;
		d[fx][fy] = c;
		for (size_t i = 0; i < 4; i++)
		{
			int tx, ty;
			tx = fx + dx[i];
			ty = fy + dy[i];
			if (tx < 1 || ty < 1 || tx > n || ty > n) continue;
			if (d[tx][ty] > c + tb[tx][ty]) {
				q.push({c + tb[tx][ty],tx,ty});
			}
		}
	}
}
