    for (int j=0; j<N; ++j) {
      for (int k=0; k<N; ++k) {
        ull tmp = p[i*2][j]+p[i*2+1][k];
        if (i==1 && V > tmp) {
          ans += c[V-tmp];
        } else {
          ++c[tmp];
        }
      }
    }
  }
  return ans;
}

