  for (long x : a[0])
    for (long y : a[1])
      ab.insert(x + y);
  for (long x : a[2])
    for (long y : a[3])
      cd.insert(x + y);
  long ct = 0;
  for (auto ab_it = ab.begin(); ab_it != ab.end();) {
    long x = *ab_it;
    auto ab_next = ab.upper_bound(x);
    if (cd.count(v - x)) {
      ct += distance(ab_it, ab_next) *
            distance(cd.lower_bound(v - x), cd.upper_bound(v - x));
    }
    auto first_past_rem = cd.lower_bound(v - x);
    if (first_past_rem == cd.begin())
      break;
    ab_it = ab.lower_bound(v - *prev(first_past_rem));
  }
  cout << ct << endl;
}

