/*
半分全列挙です。
前半と後半でそれぞれありうる金額を列挙し、map に格納します。
前半を全探索し、それに合う後半の金額を探します。
*/

int main() {
  std::cin.tie(0); std::cin.sync_with_stdio(false);
  int n; std::cin >> n;
  lint V; std::cin>>V;
  std::vector<lint> a(n),b(n),c(n),d(n);
  std::cin>>a>>b>>c>>d;
  debug(V,a,b,c,d);

  auto make_map = [&] (auto&& a, auto&& b) {
    std::map<lint,lint> map;
    for (lint x : a) for (lint y : b) {
      map[x+y]++;
    }
    return map;
  };
  auto former = make_map(a,b);
  auto latter = make_map(c,d);
  debug(former,latter);

  lint ans = 0;
  for (auto&&pair : former) {
    lint x0,y0; std::tie(x0,y0) = pair;
    lint x1 = V - x0;
    auto it = latter.find(x1);
    if (it == latter.end()) continue;
    lint y1 = it->second;
    debug(x0,y0,x1,y1);
    lint tmp = y0 * y1;
    ans += tmp;
  }
  std::cout << ans << std::endl;
  return 0;
}

