#define rep(i,s,n) for(ll i = (s); i < (n); i++)
#define rep0(i,n) rep(i,0,n)
#define rep1(i,n) rep(i,1,n+1)
#define repR(i,s,n) for(ll i = (n-1); i >= (s); i--)
#define repR0(i,n) repR(i,0,n)
#define repR1(i,n) repR(i,1,n+1)

#define BR "\n"
#define SP " "

int main() {
  ll N, V;
  cin >> N >> V;
  vector<ll> A(N), B(N), C(N), D(N);

  rep0(i, N) cin >> A[i];
  rep0(i, N) cin >> B[i];
  rep0(i, N) cin >> C[i];
  rep0(i, N) cin >> D[i];

  vector<ll> AB(N * N), CD(N * N);
  rep0(i, N) {
    rep0(j, N) {
      AB[i * N + j] = A[i] + B[j];
      CD[i * N + j] = C[i] + D[j];
    }
  }
  sort(AB.begin(), AB.end());
  sort(CD.begin(), CD.end());

  ll ans = 0, r = N * N - 1;
  rep0(i, N * N) {

    while (r >= 0 && AB[i] + CD[r] >= V) {
      if (AB[i] + CD[r] == V) {
        ll ab = upper_bound(AB.begin(), AB.end(), AB[i])
                - lower_bound(AB.begin(), AB.end(), AB[i]);
        ll cd = upper_bound(CD.begin(), CD.end(), CD[r])
                - lower_bound(CD.begin(), CD.end(), CD[r]);

        ans += ab * cd;
        i += (ab - 1);
        r -= cd;
      } else if (r > 0) {
        r--;
      } else break;
    }
    if (r < 0) break;
  }

  cout << ans << BR;
  return 0;
}

