      int c2=0;
      while(r-c2>=0 && cd[r-c2]==v-ab[l]) c2++;
      ans+=(int64)c1*c2;
      l+=c1;
      r-=c2;
    } else if(t<v){
      l++;
    } else {
      r--;
    }
  }
  printf("%lld\n",ans);
}

