#include <stdio.h>
#define N 1000
int main(void){
    int i,j,k,l;
    int n;
    long long int v;
    long long int num[4][N];
    long long int min[4],max[4];
    int ans=0;
    scanf("%d%lld",&n,&v);
    for(i=0;i<4;i++)
    {
        for(j=0;j<n;j++)
        {
            scanf("%lld",&num[i][j]);
            if(j==0) min[i]=max[i]=num[i][j];
            if(num[i][j]<min[i]) min[i]=num[i][j];
            else if(num[i][j]>max[i]) max[i]=num[i][j];
        }
    }
    for(i=0;i<n;i++)
    {
        if(num[0][i]>v-min[3]-min[2]-min[1]||num[0][i]<v-max[3]-max[2]-max[1]) continue;
        for(j=0;j<n;j++)
        {
            if(num[0][i]+num[1][j]>v-min[3]-min[2]||num[0][i]+num[1][j]<v-max[3]-max[2]) continue;
            for(k=0;k<n;k++)
            {
                if(num[0][i]+num[1][j]+num[2][k]>v-min[3]||num[0][i]+num[1][j]+num[2][k]<v-max[3]) continue;
                for(l=0;l<n;l++)
                {
                    if(num[0][i]+num[1][j]+num[2][k]+num[3][l]==v) ans++;
                }
            }
        }
    }
    printf("%lld\n",ans);
    return 0;
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.UncheckedIOException;
import java.util.Arrays;
import java.util.StringTokenizer;


class Main{
	public static void main(String[] args){
		SC sc=new SC(System.in);
		int N=sc.nextInt();
		long V=sc.nextLong();
		long[] a=new long[N];
		long[] b=new long[N];
		long[] c=new long[N];
		long[] d=new long[N];
		long[] first=new long[N*N];
		long[] sec=new long[N*N];
		for(int i=0; i<N; i++){
			a[i]=sc.nextLong();
		}
		for(int i=0; i<N; i++){
			b[i]=sc.nextLong();
		}
		for(int i=0; i<N; i++){
			c[i]=sc.nextLong();
		}
		for(int i=0; i<N; i++){
			d[i]=sc.nextLong();
		}

		for(int i=0; i<N; i++){
			for(int j=0; j<N; j++){
				first[i*N+j]=a[i]+b[j];
			}
		}
		for(int i=0; i<N; i++){
			for(int j=0; j<N; j++){
				sec[i*N+j]=c[i]+d[j];
			}
		}
		Arrays.sort(sec);
		long sum=0;
		for(int i=0; i<N; i++){
			for(int j=0; j<N; j++){
				long s=lower_bound(sec,V-first[i*N+j]);
				long t=lower_bound(sec,V-first[i*N+j]+1);
				if(s==-1){
					continue;
				}
				else if(t==-1 && sec[(int)s]==V-first[i*N+j]){
					sum+=(N*N-s);
				}
				else{
					if(sec[(int) s]==V-first[i*N+j]){
						sum+=t-s;
					}
				}
			}
		}
		pl(sum);
	}
	public static void pl(Object o) {
		System.out.println(o);
	}
	public static void pl() {
		System.out.println();
	}
	public static void p(Object o) {
		System.out.print(o);
	}
	static class SC {
		private BufferedReader reader = null;
		private StringTokenizer tokenizer = null;
		public SC(InputStream in) {
			reader = new BufferedReader(new InputStreamReader(in));
		}
		public String next() {
			if (tokenizer == null || !tokenizer.hasMoreTokens()) {
				try {
					tokenizer = new StringTokenizer(reader.readLine());
				} catch (IOException e) {
					throw new UncheckedIOException(e);
				}
			}
			return tokenizer.nextToken();
		}
		public int nextInt() {
			return Integer.parseInt(next());
		}
		public long nextLong() {
			return Long.parseLong(next());
		}
		public double nextDouble() {
			return Double.parseDouble(next());
		}
		public String nextLine() {
			try {
				return reader.readLine();
			} catch (IOException e) {
				throw new UncheckedIOException(e);
			}
		}
	}
	public static final int lower_bound(final long[] arr, final long value) {
		int low = 0;
		int high = arr.length;
		int mid;
		while (low < high) {
			mid = ((high - low) >>> 1) + low;    //(low + high) / 2 (オーバーフロー対策)
			if (arr[mid] < value) {
				low = mid + 1;
			} else {
				high = mid;
			}
		}
		if(low<arr.length) {
			return low;
		}
		else {
			return -1;
		}
	}
}

