	static class TaskX {

		public void solve(int testNumber, InputReader in, PrintWriter out) {

			int n = in.nextInt();
			long v = in.nextLong();
			long[] a = in.nextLongArray(n);
			long[] b = in.nextLongArray(n);
			long[] c = in.nextLongArray(n);
			long[] d = in.nextLongArray(n);

			Map<Long, Long> m1 = new HashMap<>();
			Map<Long, Long> m2 = new HashMap<>();

			for (int i = 0; i < n; i++) {
				for (int j = 0; j < n; j++) {
					long key = a[i]+b[j];
					m1.merge(key, 1L, Long::sum);
				}
			}
			for (int i = 0; i < n; i++) {
				for (int j = 0; j < n; j++) {
					long key = c[i]+d[j];
					m2.merge(key, 1L, Long::sum);
				}
			}

			long ans = 0;
			for (Entry<Long, Long> e : m1.entrySet()) {
				if (m2.containsKey(v - e.getKey())) {
					ans += e.getValue() * m2.get(v - e.getKey());
				}
			}

			out.println(ans);

		}
	}

