class Program
{
    static void Main()
    {
        var sw = new StreamWriter(Console.OpenStandardOutput()) { AutoFlush = false };
        Console.SetOut(sw);
        new Program().solve();
        Console.Out.Flush();
    }
    Scanner cin = new Scanner();

    int N;
    long V;
    void solve()
    {
        N = cin.nextint;
        V = cin.nextlong;

        var A = cin.scanlong;
        var B = cin.scanlong;
        var C = cin.scanlong;
        var D = cin.scanlong;

        var T = new List<long>();
        for (int i = 0; i < N; i++)
        {
            for (int j = 0; j < N; j++)
            {
                var m = A[i] + B[j];
                if (m > V - 2) continue;
                T.Add(m);
            }
        }
        var S = T.ToArray();
        Array.Sort(S);

        long ans = 0;
        for (int i = 0; i < N; i++)
        {
            for (int j = 0; j < N; j++)
            {
                var m = C[i] + D[j];
                if (m > V - 2) continue;
                ans += UpperBound(S, V - m) - LowerBound(S, V - m);
            }
        }

        Console.WriteLine(ans);
    }


    int UpperBound(long[] arr, long val)
    {
        int low = 0, high = arr.Length;
        int mid;

        while (low < high)
        {
            mid = ((high - low) >> 1) + low;
            if (arr[mid] <= val) low = mid + 1;
            else high = mid;
        }
        return low;
    }

    int LowerBound(long[] arr, long val)
    {
        int low = 0, high = arr.Length;
        int mid;

        while (low < high)
        {
            mid = ((high - low) >> 1) + low;
            if (arr[mid] < val) low = mid + 1;
            else high = mid;
        }
        return low;
    }

}


