    {
        string[] line = Console.ReadLine().Trim().Split(' ');
        var n = int.Parse(line[0]);
        var t = long.Parse(line[1]);
        line = Console.ReadLine().Trim().Split(' ');
        var w1 = Array.ConvertAll(line, long.Parse);
        line = Console.ReadLine().Trim().Split(' ');
        var w2 = Array.ConvertAll(line, long.Parse);
        line = Console.ReadLine().Trim().Split(' ');
        var w3 = Array.ConvertAll(line, long.Parse);
        line = Console.ReadLine().Trim().Split(' ');
        var w4 = Array.ConvertAll(line, long.Parse);
        var a = getDic(w1, w2, n, t);
        var b = getDic(w3, w4, n, t);
        var ans = getAns(a, b, t);
        Console.WriteLine(ans);
    }
    static long getAns(Dictionary<long, long> a, Dictionary<long, long> b, long t)
    {
        var ans = 0L;
        foreach (var x in a)
        {
            var w = t - x.Key;
            if (b.ContainsKey(w)) ans += x.Value * b[w];
        }
        return ans;
    }
    static Dictionary<long, long> getDic(long[] a, long[] b, int n, long t)
    {
        var d = new Dictionary<long, long>();
        for (int i = 0; i < n; i++)
            for (int j = 0; j < n; j++)
            {
                var w = a[i] + b[j];
                if (w < t)
                {
                    if (d.ContainsKey(w)) d[w]++;
                    else d[w] = 1;
                }
            }
        return d;
    }
}

