using System;
using System.Text;
using System.Collections.Generic;
using System.Linq;
class Solve{
    public Solve(){}
    StringBuilder sb;
    public static int Main(){
        new Solve().Run();
        return 0;
    }
    void Run(){
        sb = new StringBuilder();
        Calc();
        Console.Write(sb.ToString());
    }
    void Calc(){
        string[] str = Console.ReadLine().Split(' ');
        int N = int.Parse(str[0]);
        long V = long.Parse(str[1]);
        long[] A = new long[N];
        str = Console.ReadLine().Split(' ');
        for(int i=0;i<N;i++){
            A[i] = long.Parse(str[i]);
        }
        long[] B = new long[N];
        str = Console.ReadLine().Split(' ');
        for(int i=0;i<N;i++){
            B[i] = long.Parse(str[i]);
        }
        long[] C = new long[N];
        str = Console.ReadLine().Split(' ');
        for(int i=0;i<N;i++){
            C[i] = long.Parse(str[i]);
        }
        long[] D = new long[N];
        str = Console.ReadLine().Split(' ');
        for(int i=0;i<N;i++){
            D[i] = long.Parse(str[i]);
        }
        long[] X = new long[N*N];
        for(int i=0;i<N;i++){
            for(int j=0;j<N;j++){
                X[i*N+j] = A[i] + B[j];
            }
        }
        Array.Sort(X);
        long[] Y = new long[N*N];
        for(int i=0;i<N;i++){
            for(int j=0;j<N;j++){
                Y[i*N+j] = C[i] + D[j];
            }
        }
        Array.Sort(Y);
        long count = 0;
        {
            int p = 0;
            int q = N*N-1;
            while(p < N*N){
                long c = 1;
                while(p < N*N-1 && X[p] == X[p+1]){
                    c++;
                    p++;
                }
                long d = 0;
                while(q >= 0 && Y[q] + X[p] > V){
                    q--;
                }
                while(q >= 0 && Y[q] + X[p] == V){
                    d++;
                    q--;
                }
                p++;
                count += c*d;
            }
        }
        sb.Append(count+"\n");
    }
}

using System;
using System.Linq;//リストの使用
using System.Collections.Generic;
class Program
{
static string[] input = Console.ReadLine().Split(' ');
static long n = long.Parse(input[0]);
static long v = long.Parse(input[1]);
static long[,] nums = new long[4,n];
static long[] firstSums = new long[n*n];
static long[] secondSums = new long[n*n];
static long[,] secondOptions = new long[n*n, 2];//数値と個数
static long optionsCount = 1;//選択肢の個数
static long nowCheckNum = 0;
static long answer = 0;

	static void Main()
	{

    for(long i = 0; i < 4; i++)
    {
      long[] numGets = Array.ConvertAll(Console.ReadLine().Split(' '),long.Parse);
      for(long j = 0; j < n; j++)
      {
        nums[i,j] = numGets[j];
      }
    }

    for(long i = 0; i < n; i++)
    {
      for(long j = 0; j < n; j++)
      {
        firstSums[n*i+j] = nums[0,i] + nums[1,j];//最初の2袋の和の全通り
        secondSums[n*i+j] = nums[2,i] + nums[3,j];//後の2袋の和の全通り
      }
    }
    Array.Sort(secondSums);

    secondOptions[0,0] = secondSums[0];
    secondOptions[0,1] = 1;
    for(long i = 1; i < n*n; i++)
    {
      if(secondSums[i] == secondSums[i-1])
      {
        secondOptions[optionsCount-1, 1]++;
      }else
      {
        optionsCount++;
        secondOptions[optionsCount-1,0] = secondSums[i];
        secondOptions[optionsCount-1, 1]++;
      }
    }

    for(long i = 0; i < n*n; i++)
    {
      nowCheckNum = firstSums[i];
      Search(0, optionsCount-1);
    }

		Console.WriteLine(answer);
	}

  static void Search(long min, long max)//二分探索法で最大値を求める
  {
    while (true)
    {
      long mid = min + (max - min) / 2;
      if(Check(mid)) min = mid;//さらに大きくても成り立つかも
      else max = mid;

      if(max - min <= 1)
      {
        if(secondOptions[max,0] + nowCheckNum == v) 
        {
          answer += secondOptions[max,1];
        }
        else if(secondOptions[min,0] + nowCheckNum == v)
        {
          answer += secondOptions[min,1];
        }
      break;
      }  
    }
  }

  static bool Check(long testNum)
  {
    if(secondOptions[testNum,0] + nowCheckNum <= v) return true;
    return false;
  }


}

package main

import (
	"bufio"
	"fmt"
	"os"
	"strconv"
)

func getScanner(fp *os.File) *bufio.Scanner {
	scanner := bufio.NewScanner(fp)
	scanner.Split(bufio.ScanWords)
	scanner.Buffer(make([]byte, 500001), 500000)
	return scanner
}

func getNextString(scanner *bufio.Scanner) string {
	scanner.Scan()
	return scanner.Text()
}

func getNextInt(scanner *bufio.Scanner) int {
	i, _ := strconv.Atoi(getNextString(scanner))
	return i
}

func getNextInt64(scanner *bufio.Scanner) int64 {
	i, _ := strconv.ParseInt(getNextString(scanner), 10, 64)
	return i
}

func main() {
	fp := os.Stdin
	if len(os.Args) > 1 {
		fp, _ = os.Open(os.Args[1])
	}

	scanner := getScanner(fp)
	writer := bufio.NewWriter(os.Stdout)

	n := getNextInt(scanner)
	v := getNextInt64(scanner)

	var coins [4][]int64

	for i := 0; i < 4; i++ {
		coins[i] = make([]int64, n)
		for ii := 0; ii < n; ii++ {
			coins[i][ii] = getNextInt64(scanner)
		}
	}

	ab := make(IntMap, 1)
	cd := make(IntMap, 1)

	for i := 0; i < n; i++ {
		for ii := 0; ii < n; ii++ {
			ab.increment(coins[0][i] + coins[1][ii])
		}
	}
	for i := 0; i < n; i++ {
		for ii := 0; ii < n; ii++ {
			cd.increment(coins[2][i] + coins[3][ii])
		}
	}

	var ans int64

	for key, val := range ab {
		ans += val * cd.get(v-key)
	}
	fmt.Fprintln(writer, ans)

	writer.Flush()
}

// IntMap ...
type IntMap map[int64]int64

func (m IntMap) get(key int64) int64 {
	if _, ok := m[key]; ok {
		return m[key]
	}
	return 0
}
func (m IntMap) increment(key int64) {
	if _, ok := m[key]; ok == false {
		m[key] = 0
	}
	m[key]++
}

