#[doc = " Equivalent to std::lowerbound and std::upperbound in c++"]
pub trait BinarySearch<T> {
    fn lower_bound(&self, x: &T) -> usize;
    fn upper_bound(&self, x: &T) -> usize;
}
impl<T: Ord> BinarySearch<T> for [T] {
    fn lower_bound(&self, x: &T) -> usize {
        let mut low = 0;
        let mut high = self.len();
        while low != high {
            let mid = (low + high) / 2;
            match self[mid].cmp(x) {
                Ordering::Less => {
                    low = mid + 1;
                }
                Ordering::Equal | Ordering::Greater => {
                    high = mid;
                }
            }
        }
        low
    }
    fn upper_bound(&self, x: &T) -> usize {
        let mut low = 0;
        let mut high = self.len();
        while low != high {
            let mid = (low + high) / 2;
            match self[mid].cmp(x) {
                Ordering::Less | Ordering::Equal => {
                    low = mid + 1;
                }
                Ordering::Greater => {
                    high = mid;
                }
            }
        }
        low
    }
}
fn mk_h(xs: &[usize], ys: &[usize]) -> Vec<usize> {
    let mut v = vec![];
    for x in xs {
        for y in ys {
            v.push(x+y);
        }
    }
    v.sort();
    v
}
fn solve() {
    input! {
        N: usize, V: usize,
        X: [[usize; N]; 4],
    }
    let h1 = mk_h(&X[0], &X[1]);
    let h2 = mk_h(&X[2], &X[3]);
    let mut res = 0;
    for total1 in h1 {
        if total1 >= V { continue; }
        let more = V-total1;
        let lb = h2.lower_bound(&more);
        let ub = h2.upper_bound(&more);
        res += ub-lb;
    }
    println!("{}", res);
}
