void size1(const Ink& dropped)
{
    int x = dropped.x;
    int y = dropped.y;

    dropInk(x, y);
    dropInk(x + 1, y);
    dropInk(x - 1, y);
    dropInk(x, y + 1);
    dropInk(x, y - 1);
}

void size2(const Ink& dropped)
{
    int x = dropped.x;
    int y = dropped.y;

    size1(dropped);
    dropInk(x + 1, y + 1);
    dropInk(x + 1, y - 1);
    dropInk(x - 1, y + 1);
    dropInk(x - 1, y - 1);
}

void size3(const Ink& dropped)
{
    int x = dropped.x;
    int y = dropped.y;

    size2(dropped);
    dropInk(x, y + 2);
    dropInk(x, y - 2);
    dropInk(x + 2, y);
    dropInk(x - 2, y);
}

