#include<iostream>
using namespace std;

void base(int (*paper)[10],int x, int y) {
    if (x - 1 >= 0) {
		paper[y][x - 1]++;
	}
	if (x + 1 < 10) {
		paper[y][x + 1]++;
	}
	if (y - 1 >= 0) {
		paper[y - 1][x]++;
	}
	if (y + 1 < 10) {
		paper[y + 1][x]++;
	}
}

void sqare(int(*paper)[10], int x, int y) {
	if (x - 1 >= 0 && y - 1 >= 0) {
		paper[y - 1][x - 1]++;
	}
	if (x + 1 < 10 && y - 1 >= 0) {
		paper[y - 1][x + 1]++;
	}
	if (y + 1 < 10 && x - 1 >= 0) {
		paper[y + 1][x - 1]++;
	}
	if (y + 1 < 10 && x + 1 < 10) {
		paper[y + 1][x + 1]++;
	}
}

void slant(int(*paper)[10], int x, int y) {
	if (x - 2 >= 0) {
		paper[y][x - 2]++;
	}
	if (x + 2 < 10) {
		paper[y][x + 2]++;
	}
	if (y - 2 >= 0) {
		paper[y - 2][x]++;
	}
	if (y + 2 < 10) {
		paper[y + 2][x]++;
	}
}

int main()
{
	int paper[10][10] = {0};
	int x, y, s;
	char t;

	while (cin >> x >> t >> y >> t>> s) {
		paper[y][x]++;
		switch (s) 
		{
		    case 3:slant(paper,x, y);
		    case 2:sqare(paper,x, y);
		    case 1:base(paper,x, y);
		}
	}

	int sum = 0,max = 0;
	for (int i = 0; i < 10; i++) {
		for (int j = 0; j < 10; j++) {
			if (paper[i][j] > max) {
				max = paper[i][j];
			}
			if (paper[i][j] == 0) {
				sum++;
			}
		}
	}
	cout << sum << endl << max << endl;

	return 0;
}

