void sho(int x,int y){
  masu[y][x]=masu[y][x]+1;
  hikakub(x,y);
  for(int i=0;i<4;i++){
    dx=x+nx[i];
    dy=y+ny[i];
    if(dx<10&&dx>=0&&dy<10&&dy>=0){
      masu[dy][dx]=masu[dy][dx]+1;
      hikakub(dx,dy);
    }
  }
  return;
}
void tyu(int x,int y){
  for(int i=-1;i<2;i++){
    for(int j=-1;j<2;j++){
      if(x<10||x>=0||y<10||y>=0){
	masu[y+i][x+j]=masu[y+i][x+j]+1;
	hikakua(x,y,i,j);
      }
    }
  }
  return;
}
void dai(int x,int y){
  for(int i=-1;i<2;i++){
    for(int j=-1;j<2;j++){
      if(x<10||x>=0||y<10||y>=0){
	masu[y+i][x+j]=masu[y+i][x+j]+1;
	hikakua(x,y,i,j);
      }
    }
  }
  for(int i=0;i<4;i++){
    dx=x+nxx[i];
    dy=y+nyy[i];
    if(dx<10||dx>=0||dy<10||dy>=0){
      masu[dy][dx]=masu[dy][dx]+1;
      hikakub(dx,dy);
    }
  }
  return;
}
void hikakua(int x,int y,int i,int j){
  if(masu[y+i][x+j]==1){
    siro=siro-1;
  }
  if(maxs<masu[y+i][x+j]){
    maxs=masu[y+i][x+j];
  }
  return;
}
void hikakub(int dx,int dy){
  if(masu[dy][dx]==1){
    siro=siro-1;
  }
  if(maxs<masu[dy][dx]){
    maxs=masu[dy][dx];
  }
  return;
}

