	if(x<=8) board[x+1][y]++;
	if(y>=1) board[x][y-1]++;
	if(y<=8) board[x][y+1]++;
}

void middle(int x, int y){
	small(x, y);
	if(x>=1 && y>=1) board[x-1][y-1]++;
	if(x>=1 && y<=8) board[x-1][y+1]++;
	if(x<=8 && y>=1) board[x+1][y-1]++;
	if(x<=8 && y<=8) board[x+1][y+1]++;
}

void large(int x, int y){
	middle(x, y);
	if(x>=2) board[x-2][y]++;
	if(x<=7) board[x+2][y]++;
	if(y>=2) board[x][y-2]++;
	if(y<=7) board[x][y+2]++;
}

