				if (0 <= y - 1 + (i / 3) && y - 1 + (i / 3) <= 9) {
					if (0 <= x - 1 + (i % 3) && x - 1 + (i % 3) <= 9) {
						++lnk[x - 1 + (i % 3)][y - 1 + (i / 3)];
					}
				}
			}
		}
		else if (s == 2) {
			for (int i = 0; i < 10; ++i) {
				if (0 <= y - 1 + (i / 3) && y - 1 + (i / 3) <= 9) {
					if (0 <= x - 1 + (i % 3) && x - 1 + (i % 3) <= 9) {
						++lnk[x - 1 + (i % 3)][y - 1 + (i / 3)];
					}
				}
			}
		}
		else if (s == 1) {
			if (x - 1 >= 0)++lnk[x - 1][y];
			if (y - 1 >= 0)++lnk[x][y - 1];
			if (x + 1 < 10)++lnk[x + 1][y];
			if (y + 1 < 10)++lnk[x][y + 1];
		}
	}
	for (int i = 0; i < 10; ++i) {
		for (int j = 0; j < 10; ++j) {
			if (lnk[i][j] == 0)++cou;
			if (lnk[i][j] > max) max = lnk[i][j];
		}
	}
	printf("%d\n%d\n", cou, max);
}

