int m[14][14];

void small(int x,int y,int r) {
	m[x][y-r]++;
	m[x-r][y]++; m[x][y]++; m[x+r][y]++;
	m[x][y+r]++;
}
void middle(int x,int y) {
	for (int i=-1; i<2; i++) for (int j=-1; j<2; j++) m[x+i][y+j]++;
}
void large(int x,int y) {
	middle(x,y);
	small(x,y,2);
    m[x][y]--;
}

