bool range_check(int y, int x)
{
    if(y < 0 || x < 0 || y >= 10 || x >= 10) return false;
    else return true;
}
int main()
{
    cin.tie(0);
    ios::sync_with_stdio(false);
    int x, y, com;
    int table[10][10] = {};
    char bother;
    while(cin >> x >> bother >> y >> bother >> com)
