        if(com == 1)
        {
            for(int i = 0; i < 4; i++)
            {
                int ny = y + dy[i];
                int nx = x + dx[i];
                if(!range_check(ny, nx)) continue;
                table[ny][nx]++;
            }
        }
        if(com == 2)
        {
            for(int i = 0; i < 8; i++)
            {
                int ny = y + ddy[i];
                int nx = x + ddx[i];
                if(!range_check(ny, nx)) continue;
                table[ny][nx]++;
            }
        }
        if(com == 3)
        {
            for(int i = 0; i < 12; i++)
            {
                int ny = y + dddy[i];
                int nx = x + dddx[i];
                if(!range_check(ny, nx)) continue;
                table[ny][nx]++;
            }
        }
    }
    int cnt = 0, max_num = -INF;
    for(int i = 0; i < 10; i++)
    {
        for(int j = 0; j < 10; j++)
        {
            if(table[i][j] == 0) cnt++;
            max_num = max(table[i][j], max_num);
        }
    }
    cout << cnt << "\n" << max_num << endl;
    return 0;
}

