    if (size == 1) {
      sink(x+0,y+0);
      sink(x+1,y+0);
      sink(x-1,y+0);
      sink(x+0,y+1);
      sink(x+0,y-1);
    } else if (size == 2) {
      sink(x+0,y+0);
      sink(x+1,y+0);
      sink(x-1,y+0);
      sink(x+0,y+1);
      sink(x+0,y-1);
      sink(x+1,y+1);
      sink(x-1,y+1);
      sink(x+1,y-1);
      sink(x-1,y-1);
    } else if (size == 3) {
      sink(x+0,y+0);
      sink(x+1,y+0);
      sink(x-1,y+0);
      sink(x+0,y+1);
      sink(x+0,y-1);
      sink(x+1,y+1);
      sink(x-1,y+1);
      sink(x+1,y-1);
      sink(x-1,y-1);
      sink(x+2,y+0);
      sink(x-2,y+0);
      sink(x+0,y+2);
      sink(x+0,y-2);
