			paper[x+1][y+1]++;
			paper[x+1][y+3]++;
			paper[x+3][y+1]++;
			paper[x+3][y+3]++;
		}else if(s==3){
			paper[x+4][y+2]++;
			paper[x+2][y+4]++;
