#include <iostream>
#include <cstring>
#include <algorithm>
using namespace std;

int main(){
	int x, y, p, pt;
	string str;
	int field[10][10];
	int res_M, res_zero;

	for(int i = 0; i < 10; i++){
		for(int j = 0; j < 10; j++){
			field[i][j] = 0;
		}
	}

	while(getline(cin, str)){
		x = 0; y = 0; p = 0;
		pt = 0;

		while(pt < str.size() && str[pt] != ','){
			x = x * 10 + (str[pt] - 48);
			pt++;
		}
		pt++;
		while(pt < str.size() && str[pt] != ','){
			y = y * 10 + (str[pt] - 48);
			pt++;
		}
		pt++;
		while(pt < str.size() && str[pt] != ','){
			p = p * 10 + (str[pt] - 48);
			pt++;
		}

		//1st
		field[y][x]++;
		if(y > 0) field[y - 1][x]++;
		if(x > 0) field[y][x - 1]++;
		if(y + 1 < 10) field[y + 1][x]++;
		if(x + 1 < 10) field[y][x + 1]++;
		p--;
		if(p > 0){
			//2nd
			if(x > 0 && y > 0) field[y - 1][x - 1]++;
			if(x + 1 < 10 && y > 0) field[y - 1][x + 1]++;
			if(x > 0 && y + 1 < 10) field[y + 1][x - 1]++;
			if(x + 1 < 10 && y + 1 < 10) field[y + 1][x + 1]++;
			p--;
		}
		if(p > 0){
			//3rd
			if(x - 2 >= 0) field[y][x - 2]++;
			if(x + 2 < 10) field[y][x + 2]++;
			if(y + 2 < 10) field[y + 2][x]++;
			if(y - 2 >= 0) field[y - 2][x]++;
		}
	}

	res_M = 0; res_zero = 0;
	for(int i = 0; i < 10; i++){
		for(int j = 0; j < 10; j++){
			if(field[i][j] == 0) res_zero++;
			else res_M = max(res_M, field[i][j]);
		}
	}

	cout << res_zero << endl << res_M << endl;

	return 0;
}

#include <cstdio>

int m[10][10] = { 0 };

bool inRange(int va) {
    return (0 <= va) && (va < 10);
}

void put(int x, int y, int s) {
    switch (s) {
    case 1:
        m[x][y]++;
        if (inRange(x - 1)) {
            m[x - 1][y]++;
        }
        if (inRange(x + 1)) {
            m[x + 1][y]++;
        }
        if (inRange(y - 1)) {
            m[x][y - 1]++;
        }
        if (inRange(y + 1)) {
            m[x][y + 1]++;
        }
        break;
    case 2:
        for (int i = x - 1; i <= x + 1; ++i) {
            for (int j = y - 1; j <= y + 1; ++j) {
                if (inRange(i) && inRange(j)) {
                    m[i][j]++;
                }
            }
        }
        break;
    case 3:
        put(x, y, 2);
        if (inRange(x - 2)) {
            m[x - 2][y]++;
        }
        if (inRange(x + 2)) {
            m[x + 2][y]++;
        }
        if (inRange(y - 2)) {
            m[x][y - 2]++;
        }
        if (inRange(y + 2)) {
            m[x][y + 2]++;
        }
        break;
    }
}

int main() {
    int inx[100] = { 0 };
    int iny[100] = { 0 };
    int ins[100] = { 0 };
    int c;

    for (c = 0; scanf("%d,%d,%d", &inx[c], &iny[c], &ins[c]) != EOF; ++c) {}

    for (int i = 0; i < c; ++i) {
        put(inx[i], iny[i], ins[i]);
    }

    int mx = 0;
    int counts = 0;
    for (int i = 0; i < 10; ++i) {
        for (int j = 0; j < 10; ++j) {
            int p = m[i][j];
            if (mx < p) {
                mx = p;
            }

            if (p == 0) {
                counts++;
            }
        }
    }

    printf("%d\n%d\n", counts, mx);

    return 0;
}

