bool judge(int x,int y)
{
  if(x>=0&&x<10&&y>=0&&y<10) return true;
  else return false;     
}

int main()
{
  int map[10][10]={{0}};
  int x,y,s;
  int dep=0;
  int blank=0;
  char temp;
  while(cin>>x>>temp>>y>>temp>>s)
  {
    if(s==1)
    {
      if(judge(x-1,y)) map[x-1][y]++;
      if(judge(x+1,y)) map[x+1][y]++;
      if(judge(x,y+1)) map[x][y+1]++;
      if(judge(x,y-1)) map[x][y-1]++;
      if(judge(x,y)) map[x][y]++;        
    }                   
    else if(s==2)
    {
      for(int i=-1;i<=1;i++)
      {
        for(int j=-1;j<=1;j++)
        {
          if(judge(x+i,y+j)) map[x+i][y+j]++;       
        }        
      }     
    }
    else
    {
      for(int i=-1;i<=1;i++)
      {
        for(int j=-1;j<=1;j++)
        {
          if(judge(x+i,y+j)) map[x+i][y+j]++;       
        }        
      }   
      if(judge(x-2,y)) map[x-2][y]++;
      if(judge(x+2,y)) map[x+2][y]++;
      if(judge(x,y+2)) map[x][y+2]++;
      if(judge(x,y-2)) map[x][y-2]++;    
    }
  }
  for(int i=0;i<10;i++)
  {
    for(int j=0;j<10;j++)
    {
      if(!map[i][j]) blank++;
      if(map[i][j]>dep) dep=map[i][j];        
    }        
  }
  cout<<blank<<dep<<endl;
  //while(1);      
  return 0;    
}

#include <iostream>
#include <string>
using namespace std;
int dp[20][20];
int main(){
	for(int i=0;i<20;i++)
		for(int j=0;j<20;j++)
			dp[i][j]=-1;
	for(int i=5;i<15;i++)
		for(int j=5;j<15;j++)
			dp[i][j]=0;
	int x,y,s;
	int maxo,zero;
	char z;
	while(cin>>x>>z>>y>>z>>s){
		if(s==1){
			dp[x+5][y+5]++;
			if(dp[x-1+5][y+5]!=-1)
				dp[x-1+5][y+5]++;
			if(dp[x+1+5][y+5]!=-1)
				dp[x+1+5][y+5]++;
			if(dp[x+5][y-1+5]!=-1)
				dp[x+5][y-1+5]++;
			if(dp[x+5][y+1+5]!=-1)
				dp[x+5][y+1+5]++;
		}
		if(s==2){
			for(int i=x-1+5;i<=x+1+5;i++){
				for(int j=y-1+5;j<=y+1+5;j++){
					if(dp[i][j]!=-1)
						dp[i][j]++;
				}
			}
		}
		if(s==3){
			for(int i=x-1+5;i<=x+1+5;i++){
				for(int j=y-1+5;j<=y+1+5;j++){
					if(dp[i][j]!=-1)
						dp[i][j]++;
				}
			}
			if(dp[x-2+5][y+5]!=-1)
				dp[x-2+5][y+5]++;
			if(dp[x+2+5][y+5]!=-1)
				dp[x+2+5][y+5]++;
			if(dp[x+5][y-2+5]!=-1)
				dp[x+5][y-2+5]++;
			if(dp[x+5][y+2+5]!=-1)
				dp[x+5][y+2+5]++;
		}
		maxo=1,zero=0;
		for(int i=0;i<20;i++){
			for(int j=0;j<20;j++){
				if(dp[i][j]>maxo)
					maxo=dp[i][j];
				if(dp[i][j]==0)
					zero++;
			}
		}
	}
	cout<<zero<<endl;
	cout<<maxo<<endl;
	return 0;
}

#include <cstdio>
using namespace std;

int main() {
  int x, y, s;
  int nd[] = {5, 9, 13};
  int smallX[] = {0, -1, 0, 1, 0};
  int smallY[] = {-1, 0, 0, 0, 1};
  int mediumX[] = {-1, 0, 1, -1, 0, 1, -1, 0, 1};
  int mediumY[] = {-1, -1, -1, 0, 0, 0, 1, 1, 1};
  int largeX[] = {0, -1, 0, 1, -2, -1, 0, 1, 2, -1, 0, 1, 0};
  int largeY[] = {-2, -1, -1, -1, 0, 0, 0, 0, 0, 1, 1, 1, 2};
  int *dropX[3] = {smallX, mediumX, largeX};
  int *dropY[3] = {smallY, mediumY, largeY};
  int paper[10][10];
  int dx, dy;
  int count;
  int max;

  for (int i=0; i<10; i++) {
    for (int j=0; j<10; j++) {
      paper[i][j] = 0;
    }
  }

  while (scanf("%d,%d,%d", &x, &y, &s) == 3) {
    --s;
    for (int i=0; i<nd[s]; i++) {
      dx = x + dropX[s][i];
      dy = y + dropY[s][i];
      if (dx>=0 && dx<10 && dy>=0 && dy<10) ++paper[dx][dy];
    }
  }

  count = 0;
  max = 0;
  for (int i=0; i<10; i++) {
    for (int j=0; j<10; j++) {
      if (!paper[i][j]) count++;
      if (paper[i][j] > max) max = paper[i][j];
    }
  }
  printf("%d\n", count);
  printf("%d\n", max);

  return 0;
}

#include <iostream>
#include <cstdio>
using namespace std;

int board[10][10];

void fill(int x, int y){
	if(0<=x && x<10 && 0<=y && y<10){
		board[y][x]++;
	}
}

int main(){
	int x, y, size;
	while(scanf("%d,%d,%d", &x, &y, &size)+1){
		if(size >= 1){ // small
			fill(x, y);
			fill(x+1, y);
			fill(x-1, y);
			fill(x, y+1);
			fill(x, y-1);
		}
		if (size >= 2) { // medium
			fill(x+1, y+1);
			fill(x+1, y-1);
			fill(x-1, y+1);
			fill(x-1, y+1);
		}
		if(size >= 3){ // large
			fill(x+2, y);
			fill(x-2, y);
			fill(x, y+2);
			fill(x, y-2);
		}
	}
	int num_zero = 0;
	int deepest = 0;
	for (int i = 0; i < 10; i++) {
		for (int j = 0; j < 10; j++) {
			if(board[j][i] == 0){
				num_zero++;
			}
			if(deepest < board[j][i]){
				deepest = board[j][i];
			}
		}
	}
	cout << num_zero << endl;
	cout << deepest << endl;

	return 0;
}

