int p[10][10];

void drop(int x, int y) {
  if (0 <= x && x < 10 && 0 <= y && y < 10)
    p[x][y]++;
}

int main() {
  int x, y, s;
  while (scanf("%d,%d,%d", &x, &y, &s) == 3)
    switch (s) {
    case 3:
      drop(x-2, y);
      drop(x+2, y);
      drop(x, y-2);
      drop(x, y+2);
    case 2:
      drop(x-1, y-1);
      drop(x-1, y+1);
      drop(x+1, y-1);
      drop(x+1, y+1);
    case 1:
      drop(x-1, y);
      drop(x+1, y);
      drop(x, y);
      drop(x, y-1);
      drop(x, y+1);
