  char c1, c2;
  while (cin >> x >> c1 >> y >> c2 >> s) {
    if (s == 1) {
      board[x][y]++;
      rep(i, 4) {
        int dx = x + (i - 1) % 2;
        int dy = y + (i - 2) % 2;
        if (!check(dx, dy)) continue;
        board[dx][dy]++;
      }
    } else if (s == 2) {
      for (int i = -1; i <= 1; i++)
      for (int j = -1; j <= 1; j++) {
        int dx = x + i;
        int dy = y + j;
        if (!check(dx, dy)) continue;
        board[dx][dy]++;
      }
    } else {
      for (int i = -2; i <= 2; i++)
      for (int j = -2; j <= 2; j++) {
        if (abs(i) + abs(j) > 2) continue;
        int dx = x + i;
