#include <iostream>
#include <cstdio>
#include <climits>
using namespace std;

int board[10][10];
int empty = 0;
int _max = INT_MIN;

bool ok(int val) {
    return(val >= 0 && val < 10);
}
void small(int x, int y) {
    board[y][x]++;
    int north = y - 1, south = y + 1,  east = x + 1, west = x - 1;
    if(ok(north)) board[north][x]++;
    if(ok(south)) board[south][x]++;
    if(ok(east))  board[y][east]++;
    if(ok(west))  board[y][west]++;
}
void mid(int x, int y) {
    small(x, y);
    int uprightx = x + 1, uprighty = y + 1;
    int downrightx = x + 1, downrighty = y - 1;
    int downleftx = x - 1, downlefty = y - 1;
    int upleftx = x - 1, uplefty = y + 1;
    if(ok(uprightx) && ok(uprighty)) board[uprighty][uprightx]++;
    if(ok(downrightx) && ok(downrighty)) board[downrighty][downrightx]++;
    if(ok(downleftx) && ok(downlefty)) board[downrighty][downrightx]++;
    if(ok(upleftx) && ok(uplefty)) board[uplefty][upleftx]++;
}
void large(int x, int y) {
    mid(x, y);
    int north = y - 2, south = y + 2,  east = x + 2, west = x - 2;
    if(ok(north)) board[north][x]++;
    if(ok(south)) board[south][x]++;
    if(ok(east))  board[y][east]++;
    if(ok(west))  board[y][west]++;
}
void search() {
    for(int r = 0; r < 10; r++) {
        for(int c = 0; c < 10; c++) {
            if(board[r][c] > _max) _max = board[r][c];
            if(board[r][c] == 0) empty++;
        }
    }
}

int main(void) {
    int x, y, size;

    while(scanf("%d,%d,%d", &x, &y, &size) != EOF) {
        switch(size) {
            case 1: small(x, y); break;
            case 2: mid(x, y);   break;
            default:large(x, y); break;
        }
    }
    search();
    cout << empty << endl << _max << endl;
    return(0);
}

#include <utility>
#include <iostream>
#include <algorithm>
#include <cstdio>

class Paper
{
private :
    int data [10] [10];
private :
    static auto isValidX (int x) -> bool
    {
        return 0 <= x && x < 10;
    }
    static auto isValidY (int y) -> bool
    {
        return 0 <= y && y < 10;
    }
public :
    Paper ()
        : data {{{}}}
    {
    }
    auto increase (int x , int y) -> void
    {
        if (isValidX (x) && isValidY (y))
        {
            ++ data [x] [y];
        }
    }
    auto dropSmall (int x, int y) -> void
    {
        increase (x , y);
        increase (x - 1 , y);
        increase (x + 1 , y);
        increase (x , y - 1);
        increase (x , y + 1);
    }
    auto dropMidium (int x , int y) -> void
    {
        dropSmall (x , y);
        increase (x - 1 , y - 1);
        increase (x + 1 , y - 1);
        increase (x - 1 , y + 1);
        increase (x + 1 , y + 1);
    }
    auto dropLarge (int x , int y) -> void
    {
        dropMidium (x , y);
        increase (x - 2 , y);
        increase (x + 2 , y);
        increase (x , y - 2);
        increase (x , y + 2);
    }
    auto getResult () -> std::pair <int , int>
    {
        int zeroCellNum = 0;
        int maxValue = 0;
        for (auto && line : data)
        {
            for (auto && elem : line)
            {
                zeroCellNum += (elem == 0);
                maxValue = std::max (maxValue , elem);
            }
        }
        return std::make_pair (zeroCellNum , maxValue);
    }
};

auto main () -> int
{
    Paper paper;
    int x , y , size;
    while (std::scanf ("%d,%d,%d" , & x , & y , & size) == 3)
    {
        switch (size)
        {
        case 1:
            paper.dropSmall (x , y);
            break;
        case 2:
            paper.dropMidium (x , y);
            break;
        case 3:
            paper.dropLarge (x , y);
            break;
        }
    }
    auto res = paper.getResult ();
    std::cout << res.first << std::endl;
    std::cout << res.second << std::endl;
}

