        if (scanf("%d,%d,%d", &x, &y, &s) == EOF) break;
        if (cin.eof()) break;
        p[x][y]++;
        if (s == 1) {
            F(x + 1, y    );
            F(x - 1, y    );
            F(x    , y + 1);
            F(x    , y - 1);
