            F(x + 1, y + 1);
            F(x + 1, y    );
            F(x + 1, y - 1);
            F(x    , y + 1);
            F(x    , y - 1);
            F(x - 1, y + 1);
            F(x - 1, y    );
            F(x - 1, y - 1);
        } else {
            F(x + 1, y + 1);
            F(x + 1, y    );
            F(x + 1, y - 1);
            F(x    , y + 1);
            F(x    , y - 1);
            F(x - 1, y + 1);
            F(x - 1, y    );
            F(x - 1, y - 1);
            F(x + 2, y    );
            F(x - 2, y    );
            F(x    , y + 2);
            F(x    , y - 2);
        }
    }
    int e = 0, m = 0;
    for (int i = 0; i < 10; i++) {
        for (int j = 0; j < 10; j++) {
            if (p[i][j] == 0) e++;
            m = max(m, p[i][j]);
        }
    }
    cout << e << endl;
    cout << m << endl;
    return 0;
}

