    if(s == 1) {
      cell[x][y-1]++;
      cell[x-1][y]++;
      cell[x][y]++;
      cell[x+1][y]++;
      cell[x][y+1]++;
    }
    else if(s == 2) {
      FOR(i,-1,2) {
	FOR(j,-1,2) {
	  cell[x+i][y+j]++;
	}
      }
    }
    else if(s == 3) {
      cell[x][y-2]++;
      cell[x-2][y]++;
      cell[x+2][y]++;
      cell[x][y+2]++;
      FOR(i,-1,2) {
	FOR(j,-1,2) {
	  cell[x+i][y+j]++;
	}
      }
    }
  }

  int numzero = 0;
  int maxvalue = 0;
  REP(i,10) {
    REP(j,10) {
