int main(){
    int x,y,s,ans_zero=0,ans_max=0;

    while(EOF!=scanf("%d,%d,%d",&x,&y,&s)){

        switch (s) {
            case 1: //small
                drop(x-1,y);
                drop(x,y-1);
                drop(x+1,y);
                drop(x,y+1);
                drop(x,y);
                break;

            case 3:
                drop(x+2,y);
                drop(x,y+2);
                drop(x-2,y);
                drop(x,y-2);

            case 2: //medium
                for(int i=x-1;i<=x+1;i++){
                    for(int j=y-1;j<=y+1;j++){
                        drop(i,j);
                    }
                }
