    for(int i=0;i<10;i++){
        for(int j=0;j<10;j++){
            if(paper[i][j]==0)
                ans_zero++;
            ans_max=max(ans_max,paper[i][j]);
        }
    }

    printf("%d\n%d\n",ans_zero,ans_max);

    return 0;
}

