int count(int data[][W], int height, int width, int key){

	int ret = 0;
	int i, k;

	for(i=0; i<height; i++){

		for(k=0; k<width; k++){

			if(data[i][k] == key){

				ret++;

			}

		}

	}

	return ret;

}


int getMax(int data[][W], int height, int width){

	int max = data[0][0];
	int i, k;

	for(i=0; i<height; i++){

		for(k=0; k<width; k++){

			if(max < data[i][k]){

				max = data[i][k];

			}

		}

	}

	return max;

}



void drop(int data[][W], int height, int width, int x, int y, int size){

	switch(size){

		case 1:
			data[x][y]++;

			if(x>0){

				data[x-1][y]++;

