			}

			if(y>0){

				data[x][y-1]++;

			}

			if(y<height-1){

				data[x][y+1]++;

			}

			if(x>0 && y>0){

				data[x-1][y-1]++;

			}

			if(x>0 && y<height-1){

				data[x-1][y+1]++;

			}

			if(x<width-1 && y>0){

				data[x+1][y-1]++;

			}

			if(x<width-1 && y<height-1){

				data[x+1][y+1]++;

			}


		break;


		case 3:
			data[x][y]++;

			if(x>0){

				data[x-1][y]++;

			}

			if(x<width-1){

				data[x+1][y]++;

			}

			if(y>0){

				data[x][y-1]++;

			}

