draw_ink(int x,int y,int siz)
{
  switch(siz){
  case 3:
    if(x>1)             paper[x-2][y]++;
    if(x<XSIZ-2)        paper[x+2][y]++;
    if(y>1)             paper[x][y-2]++;
    if(y<XSIZ-2)        paper[x][y+2]++;
  case 2:
    if((x>0)     &&(y>0))           paper[x-1][y-1]++;
    if((x<XSIZ-1)&&(y>0))           paper[x+1][y-1]++;
    if((x>0)     &&(y < YSIZ-1))    paper[x-1][y+1]++;
    if((x<XSIZ-1)&&(y < YSIZ-1))    paper[x+1][y+1]++;
  default:
    paper[x][y]++;
    if(x > 0)           paper[x-1][y]++;
    if(x < XSIZ-1)      paper[x+1][y]++;
    if(y > 0)           paper[x][y-1]++;
    if(y < YSIZ-1)      paper[x][y+1]++;
  }
}
